'use strict';

(function () {
    // loading 打开
    function openLoading() {
        $('.loading').toggleClass('loading-hidden');
        setTimeout(function () {
            $('.loading').toggleClass('loading-open');
        }, 25);
    }

    // loading 隐藏
    function closeLoading() {
        $('.loading').addClass('loading-close');
        setTimeout(function () {
            $('.loading').toggleClass('loading-hidden loading-open').removeClass('loading-close');
        }, 500);
    }

    // 测试 token
    var token = $('#card-container').data('token');
    // var clientToken = "";

    var $CardImage = $('#card-type'),
        $WaringInfo = $('.warning-info');

    var checkout = {}; // 事件 句柄

    braintree.setup(token, "custom", {
        id: "card-container",
        onReady: function onReady(integration) {
            checkout = integration;
            console.log(checkout);
        },
        onError: function onError(error) {
            if (error.type === 'VALIDATION') {
                $WaringInfo.children('span').html(error.message);
                $WaringInfo.removeClass('off');
            } else {
                console.error(error.message);
            }
        },
        onPaymentMethodReceived: function onPaymentMethodReceived(payload) {
            console.info('payload : ');
            console.info(payload);
            openLoading();
            // TODO
            $.ajax({
                url: '/braintree',
                type: 'POST',
                data: { nonce: payload.nonce }
            }).done(function (data) {
                console.log("success");
                if (data.success) {
                    var $InfoForm = $('#infoForm');
                    if ($InfoForm === undefined) {
                        window.location.href = data.redirectUrl;
                    } else {
                        $InfoForm.submit();
                    }
                }
            }).fail(function () {
                console.log("error");
            }).always(function () {
                console.log("complete");
                closeLoading();
            });
        }
    });
})();
